## Part 2
cumsum = 0
for i in range(len(directions)):
	if directions[i] == '(':
		cumsum += 1
	elif directions[i] == ')':
		cumsum -= 1
	if cumsum == -1:
		print(i+1) # Index starts at 1
		break
